import * as actionTypes from '../../components/Common/actions/actionTypes/actionTypes.js';
import { takeLatest, put } from 'redux-saga/effects';
import callBff from '../callBff.js'

import Airtable from "../data/airtable";
import Orders from "../data/orders";
import Items from "../data/items";
import Addons from "../data/addons";



export function* sendSms(message) {
  const res = yield callBff(`ordering/sms`, 'POST', { message })
      .then(response => response)
      yield put({
        type: actionTypes.SEND_SMS_SUCCESS,
        res,
      })
};

export function* getCompleteOrder(message) {
  const venueName = "Hopscotch";
    const orderState = {}
    yield Airtable.fetchBase({
      venueName,
      baseName: "Orders"
    }).then(response => {
      Orders.setAll({
        venueName,
        items: response
      });
      const currentOrders = Orders.getAll("Hopscotch");
      orderState.orders = currentOrders;
    });

    /**
     * Get all current available items to map with the order.
     */
    yield Airtable.fetchBase({
      venueName,
      baseName: "Database"
    }).then(response => {
      Items.setAll({
        items: response.map(Airtable.mapItemColumns),
        venueName
      });
      const currentItems = Items.getAll("Hopscotch");
      orderState.items = currentItems;
    });

    /**
     * Get all current available items to map with the order.
     */
    yield Airtable.fetchBase({
      venueName,
      baseName: "Add-On by Group"
    }).then(response => {
      Addons.setAll({
        addons: response.map(Airtable.mapAddonColumns),
        venueName
      });
      const currentAddons = Addons.getAll("Hopscotch");
      orderState.addons = currentAddons;
    });

    yield put({
      type: actionTypes.GET_ORDER_SUCCESS,
      orderState,
    })
};

export function* actionWatcher() {
  yield [
    takeLatest(actionTypes.SEND_SMS_REQUEST, sendSms),
    takeLatest(actionTypes.GET_ORDERS_REQUEST, getCompleteOrder),
  ]
}
