import React from 'react';
import OrderRow from './OrderRow.js';

class OrderPanel extends React.Component{

    groupByOrderId(orders){
        const orderIds = [];
        const groupedOrders = {};
        orders.map(order => {
            if(!orderIds.includes(order.id)){
                orderIds.push(order.id)
                groupedOrders[order.id] = [order];
            }
            else{
                groupedOrders[order.id].push(order); 
            }
        });


        const {customer_details, table_or_pickup, phone_number, created_time} = groupedOrders[orderIds[0]][0];
        return orderIds.map(orderId => {
            const { items } = groupedOrders[orderId];
            return (
                <OrderRow
                    customerName={customer_name}
                    tableOrPickup={table_or_pickup}
                    items={items}
                    phoneNumber={phone_number}
                    createdTime={created_time}
                />
            )
        })




    }
    renderOrderItem() {
        const {orders} = this.props;
        return this.groupByOrderId(orders);
    }

    render(){
        const {header, hasOrders, orders, label, renderOrderItem} = this.props;

        const noOrderStyles = {
            padding: "20px"
        };

        return(
            <div style={{marginTop: '80px'}} id={label}>
                <div style={{marginLeft: '20px', paddingTop:'36px'}}>
                <h4>{header}</h4>
                </div>
                {true ? orders.map(this.renderOrderItem): (
                    <div style={noOrderStyles}>
                        {`No ${label} orders yet.`}
                    </div>
                )}
          </div>
        );
    }
}

export default OrderPanel;
