import React from 'react';
import socketIOClient from "socket.io-client";
import {connect} from 'react-redux'
import {bindActionCreators} from 'redux'

import * as actions from './actions/actions.js';

class SocketListener extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            current: false,
            previous: false,
            endpoint: "localhost:5000",
        }
    }

    componentWillMount(){
        const {  getOrders, orders } = this.props;
        getOrders();
     }

    componentDidMount(){
        const { endpoint, current } = this.state;
        const socket = socketIOClient(endpoint);
        socket.on("FromAPI", data => this.setState({ previous: current, current: data },
            this.checkUpdate(current, data)));
    }

    checkUpdate(previous, current){
        const { refresh } = this.props;
        if(previous !== current){
          refresh();
        }
    }

    render(){

        const { orders, isLoading, sendSms, updateAirtable, getOrders } = this.props;
        const pendingOrders = orders.pending;
        const completedOrders = orders.completed;
        const hasPendingOrders = !!pendingOrders.length;
        const hasCompletedOrders = !!completedOrders.length;

        return (
            <OrderPanel
                  hasOrders={hasPendingOrders}
                  orders={pendingOrders}
                  label='pending'
                  header="Pending Orders"
                  sendSms={sendSms}
                  updateAirtable={updateAirtable}
                  channel={this.channel}
            />
        );
    }

} 


const mapDispatchToProps = dispatch => bindActionCreators(actions, dispatch);

const mapStateToProps = state => ({
  router: state.router,
  orders: state.common.orders,
  isLoading: state.common.isLoading,
});

export default connect(mapStateToProps, mapDispatchToProps)(SocketListener);
