import React from 'react';

class AddOnContainer extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            addonState: [],
        };

        this.selectAddon = this.selectAddon.bind(this);
    }

    selectAddon(){
        const { details, onSelect } = this.props;
        const { selected } = this.state;

        if(selected){
            this.setState({selected: false});
        }
        else{
            this.setState({selected: true});
        }

        onSelect(details);

    }

    render(){
        const { addons } = this.props;

    return (
      <div>
      <p>Add-Ons</p>
        {addons.map(addOn => (
          <AddOn details={addOn.AIRTABLE_MENU_PAYLOAD.fields} onSelect={this.selectAddOn}/>))}
      </div>
    );
    }   
}

export default AddOnContainer;
