import React from 'react';
import AddOn from './AddOn.js';

class AddOnContainer extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            addonState: [],
        };

        this.selectAddOn = this.selectAddOn.bind(this);
    }

    selectAddOn(addOnDetails){
        const { selected } = this.state;

        if(selected){
            console.log(addOnDetails)
        }

        //onSelect(details);

    }

    render(){
        const { addons } = this.props;

    return (
      <div>
      <p>Add-Ons</p>
        {addons.map(addOn => (
          <AddOn details={addOn.AIRTABLE_MENU_PAYLOAD.fields} onSelect={this.selectAddOn}/>))}
      </div>
    );
    }   
}

export default AddOnContainer;
