import React from 'react';
import OrderRow from './OrderRow.js';

class OrderPanel extends React.Component{

    renderOrderItem(order) {
        if (!!order.items) {
          return (
            <OrderRow
              key={order.id}
              order={order}
              updateOrder={this.updateOrderItem}
            />
          );
        }
        return null;
      }

    render(){
        const {header, hasOrders, orders, label, renderOrderItem} = this.props;

        const noOrderStyles = {
            padding: "20px"
        };

        return(
            <div style={{marginTop: '80px'}} id={label}>
                <div style={{marginLeft: '20px', paddingTop:'36px'}}>
                <h4>{header}</h4>
                </div>
                {hasOrders ? orders.map(this.renderOrderItem): (
                    <div style={noOrderStyles}>
                        {`No ${label} orders yet.`}
                    </div>
                )}
          </div>
        );
    }
}

export default OrderPanel;
