import React from 'react';

class CartItem extends React.Component{
    constructor(props){
        super(props);

    }


    getAddonSubtotal(addOns){
        let subtotal = 0;
        addOns.map(addOn => {
            subtotal = subtotal + addOn.price;
        })

        return subtotal;
    }

    getCostDetails(items){
        let quantity = 0;
        let subtotal = 0;
        items.map(item => {
            quantity = quantity + item.quantity; 
            subtotal = subtotal + (quantity * this.getAddonSubtotal(item.addOns));
        });
        return { quantity, subtotal };
    };



    printItem(){
        const {items} = this.props;
